body{
    display: flex;
    height: 100vh;
    align-items: center;
    justify-content: center;
}

.wrapper{
    display: inline-flex;
}

.wrapper .icon{
    margin: 0 20px;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    flex-direction: column;
    position: relative;
    z-index: 2;
}

.wrapper .icon span{
    position: relative;
    height: 60px;
    width: 60px;
    display: block;
    background-color: #fff;
    box-shadow: 0 10px 10px rgba(0,0,0,0.1);
    border-radius: 50%;
    display: flex;
    z-index: 2;
    align-items: center;
    justify-content: center;
}

.wrapper .icon img{
    margin-left: auto;
    margin-right: auto;
}

.wrapper .icon .tooltip{
    position: absolute;
    top: 0px;
    background-color: #fff;
    box-shadow: 0 10px 10px rgba(0,0,0,0.1);
    font-size: 20px;
    padding: 10px 18px;
    border-radius: 25px;
    opacity: 0;
    pointer-events: none;
    transition: all 0.4s cubic-bezier(0.68,-0.55, 0.265, 1.55);
}

.wrapper .icon:hover .tooltip{
    top: -70px;
    opacity: 1;
    pointer-events: auto;
}

.wrapper .icon .tooltip::before{
    position: absolute;
    content: "";
    height: 15px;
    width: 15px;
    background: rgb(53, 104, 104);
    bottom: -8px;
    left: 50%;
    transform: translateX(-50%) rotate(45deg);
}

.wrapper .icon:hover span{
    text-shadow: 0px -1px 0px rgba(0,0,0,0.4);
}

.wrapper .facebook:hover span,
.wrapper .facebook:hover .tooltip,
.wrapper .facebook:hover .tooltip:before{
    background: rgb(53, 104, 104);
    transition: all 0.4s cubic-bezier(0.68,-0.55, 0.265, 1.55);
}

